import React from 'react';
import { Input, Form, Button, Card, Avatar } from 'antd';
import Icon from 'antd/lib/icon';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [{
        User: {
            id: 1,
            nickname: '코코하세요',
        },
        content: '첫 번째 게시글',
        img: 'https://web-source-animalcro-project.s3.ap-northeast-2.amazonaws.com/source/loading.png',
    }],
}
const Home = () => {
    return (
        <>
            {dummy.isLoggedIn && <Form encType="multipart/form-data">
                <Input.TextArea maxLength="140" placeholder="어떤 신기한 일이 있었나요?" />
                <div>
                    <Input type="file" multiple hidden />
                    <Button>이미지 업로드</Button>
                    <Button type="primary" stype={{ float: 'right' }} htmlType="submit">짹짹</Button>
                </div>
                <div>
                    {dummy.imagePaths.map((v, i) => {
                        return (
                            <div key={i} stype={{ display: 'inline-block' }}>
                                <img src={'http://localhost:3065/' + v} style={{ width: '200px' }} alt={v} />
                                <div>
                                    <Button>제거</Button>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </Form>}
            {dummy.mainPosts.map((c) => {
                return (
                    <Card
                        key={+c.createdAt}
                        cover={c.img && <img alt="example" src={c.img} />}
                        actions={[
                            <Icon type="retweet" key="retweet" />,
                            <Icon type="heart" key="heart" />,
                            <Icon type="message" key="message" />,
                            <Icon type="ellipsis" key="ellipsis" />
                        ]}
                        extra={<Button>팔로우</Button>}
                    >
                        <Card.Meta
                            avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                            title={c.User.nickname}
                            description={c.content}
                        />
                    </Card>
                );
            })}
        </>
    );
};

export default Home;
